import React, { useEffect, useState } from 'react';
import superagent from 'superagent';
import { makeStyles } from '@material-ui/core';
import Table from '../../components/Table/BasicTable';

const useStyles = makeStyles({});

const columns = [
  {
    id: 'name',
    label: 'Name',
  },
  {
    id: 'episode',
    label: 'Episode',
  },
  {
    id: 'air_date',
    label: 'Air date',
  }
];

const EpisodePage = () => {
  const classes = useStyles();

  const [episode, SetEpisode] = useState([]);
  const [info, setInfo] = useState();
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(20);

  useEffect(() => {
    superagent.get('https://rickandmortyapi.com/api/episode').then(response => {
      SetEpisode(response.body.results);
    })
    .catch(error => {
      console.error(error)
    });
  }, [SetEpisode]);

  useEffect(() => {
    superagent.get('https://rickandmortyapi.com/api/episode').then(response => {
      setInfo(response.body.info);
    })
    .catch(error => {
      console.error(error)
    });
  }, [setInfo]);

  console.log(episode, 'episode');
  console.log(info, 'info');

  return (
    <div className={classes.container}>
      <h2 className={classes.title}>Episode page</h2>
      <Table
        page={page}
        columns={columns}
        rowsPerPage={rowsPerPage}
        rows={episode}
        handleChangePage={handleChangePage}
        handleChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </div>
  );
};

export default EpisodePage;
