import React, { useEffect, useState } from 'react';
import superagent from 'superagent';
import { makeStyles } from '@material-ui/core';
import Table from '../../components/Table/BasicTable';
import CharacterModal from '../../components/Modals/CharacterModal';

const useStyles = makeStyles({});

const columns = [
    { 
    id: 'name',
    label: 'Name',
    },
    { 
      id: 'gender',
      label: 'Gender',
      },
    {
    id: 'status',
    label: 'Status',
  },
  {
    id: 'species',
    label: 'Species'
  }
];

const CharacterPage = () => {
  const classes = useStyles();

  const [character, SetCharacter] = useState([]);
  const [info, setInfo] = useState();
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(20);
  const [open, setOpen] = useState(false);
  const [charactersItem, setCharactersItem] = useState(null);

  useEffect(() => {
    superagent.get('https://rickandmortyapi.com/api/character').then(response => {
      SetCharacter(response.body.results);
    })
    .catch(error => {
      console.error(error)
    });
  }, [SetCharacter]);

  useEffect(() => {
    superagent.get('https://rickandmortyapi.com/api/character').then(response => {
      setInfo(response.body.info);
    })
    .catch(error => {
      console.error(error)
    });
  }, [setInfo, page]);

  useEffect(() => {
    if(info.next) {
      superagent.get(`https://rickandmortyapi.com/api/character?${page}`).then(response => {
      setNextPage(response.body.info.next);
    })
    .catch(error => {
      console.error(error)
    });
  };
    if(info.prev) {
      superagent.get(`https://rickandmortyapi.com/api/character?${page}`).then(response => {
        setNextPage(response.body.info.prev);
      })
      .catch(error => {
        console.error(error)
      });
    }
    
  }, [setNextPage, page, setPrevPage]);

  const handleChangePage = (event, newPage) => {
    console.log(newPage, 'newPage');
    setPage(newPage + 1);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  const handleClickOpen = (id) => {
     const foundCh = character.find((ch) => ch.id === id);

     
     if (foundCh) {
       setOpen(true);
       setCharactersItem(foundCh);
     }
   };

  const handleClickClose = () => {
    setOpen(false);
    setCharactersItem(null);
  };

  console.log(info, 'info');
  console.log(character, 'character');

  return (
    <>
    <div className={classes.container}>
      <h2 className={classes.title}>Character page</h2>
      <Table
        page={page}
        columns={columns}
        rowsPerPage={rowsPerPage}
        rows={character}
        count={info && info.count}
        handleChangePage={handleChangePage}
        handleChangeRowsPerPage={handleChangeRowsPerPage}
        handleOpen={handleClickOpen}
      />
    </div>
      <CharacterModal 
        charactersItem={charactersItem}
        open={open}
        handleClose={handleClickClose}
      />
  </>        
  );
};

export default CharacterPage;
